import React from 'react';
import './css/Form.css';
import Input from './Input';

export default class Form extends React.Component{

    constructor(props){
        super(props);
        this.state = { email: 'teste@teste.com', password: '123456', type: 'password' };
        this.set = this.set.bind(this);
    }

    set(value, typeInput){
        if(typeInput === 'email'){
            this.setState({email: value});
        }else{
            this.setState({password: value});
        }
    }

    changeVisibilityPass(){
        let type = '';
        if(this.state.type === 'password'){
            type = 'text';
        }else{
            type = 'password';
        }
        this.setState({type});
    }

    render(){
        /*
            <input type="text" value={this.state.email}
                        onChange={() => {this.setEmail(this.value)}}/>
                    <p>Senha</p>
                    <input type={this.state.type} value={this.state.senha}
                        onChange={() => {this.setSenha(this.value)}}/>
        */
        return(
            <header className="form-render">
                <p>Orange</p>
                <form>
                    <p>Email</p>
                    <input type="checkbox" onChange={() => {this.changeVisibilityPass()}}/> Mostrar senha
                    <Input type='text' value={this.state.email} typeInput='email' set={this.set}/>
                    <p>Problemas para acessar a conta ?</p>
                    <input type="button" value="Acessar" />
                </form>
                <p>ou</p>
                <input type="button" value="Cadastrar" />
            </header>
        );
    }
}
