import React from 'react';
import FlexView from 'react-flexview';
import './css/MenuTop.css';
import { Col, Row } from 'react-flexbox-grid';
import OptionMenu from './OptionMenu';

export default class MenuTop extends React.Component {

    constructor(props){
        super(props);
        this.state = { optSel: 'PLACES' };
        this.setOptSel = this.setOptSel.bind(this);
    }

    setOptSel(optSel){
        this.setState({optSel});
    }

    render(){
        return(
            <FlexView hAlignContent='center' vAlignContent='center' className='container-menu'>
                <Row>
                    <Col>
                        <img src={require('../assets/logo.png')} className='logo'/>
                    </Col>
                    <Col>
                        <Col>
                            <OptionMenu sel={this.state.optSel} setSel={this.setOptSel} value='FOODS'/>
                        </Col>
                        <Col>
                            <OptionMenu sel={this.state.optSel} setSel={this.setOptSel} value='PEOPLE'/>
                        </Col>
                        <Col>
                            <OptionMenu sel={this.state.optSel} setSel={this.setOptSel} value='PLACES'/>
                        </Col>
                    </Col>
                </Row>
            </FlexView>
        );
    }
}
