import React from 'react';
import './css/Content.css';
import Parse from 'parse';
import MenuTop from './MenuTop';
import FlexView from 'react-flexview';
import { Col, Row } from 'react-flexbox-grid';
import ItemList from './ItemList';

export default class Content extends React.Component {

    constructor(props){
        super(props);
        this.state = { listSel: 'FOODS' };
        this.setItens = this.setItens.bind(this);
    }

    componentWillMount(){
        Parse.serverURL = 'http://localhost:1337/parse';
        Parse.initialize('OSGiFZBrXxNLjN3gYDPsgi7P4a0j6fzcc2iaCKga', 'k8xm42UVuIP51wR2DswLY8NL3zgWfev8AuKUUjga')
        this.checkLogIn();
    }

    checkLogIn(){
        let url = window.location.href;
        if(url.indexOf('?') != -1){
            let cont = url.split('?');
            let email = atob(cont[1]);
            let password = atob(cont[2]);
            Parse.User.logIn(email, password)
            .then(
                (result) => {},
                (err) => {
                    alert("Error: " + err.message);
                    window.location.href = 'http://localhost:3000';
                }
            );
        }else{
            window.location.href = 'http://localhost:3000';
        }
    }

    setItens(optSel){
        this.setState({listSel: optSel});
    }

    renderItems(){
        var result = 'getAllFoods';
        switch(this.state.listSel){
            case 'FOODS':
                result = 'getAllFoods';
                break;
            case 'PEOPLE':
                result = 'getAllPeople';
                break;
            case 'PLACES':
                result = 'getAllPlaces';
                break;
        }

        Parse.Cloud.run(result).then(
            (resp) => {
              var foods = resp;
              console.log(foods);
            }, err => {
              console.log('erro' + err);
            }
        );
        var items = [];
        for (var i = 0; i < 6; i++) {
            items.push(<ItemList key={i} title="teste" background="red"/>);
        }
        return(items);
    }

    render(){
        //'149vmin'
        //margin-right: 5.5vmin;
        /*
            <ItemList title="Teste 1" background="yellow" />
                                    <ItemList title="Teste 2" background="green"  />
                                    <ItemList title="Teste 3" background="blue"   />
                                    <ItemList title="Teste 4" background="black"  />
                                    <ItemList title="Teste 5" background="brown"  />
                                    <ItemList title="Teste 6" background="purple" />
        */
        return(
            <div className="content">
                    <MenuTop setItens={this.setItens} />
                    <FlexView vAlignContent="top" hAlignContent="center" className="view-content">
                        <Col style={{marginLeft: '12vmin'}}>
                            <Row>
                                <p style={{
                                    width: '134vmin', 
                                    fontFamily: 'open sans semibold', 
                                    color: '#4A4A4A',
                                    fontSize: '2.7vmin'
                                    }}>LIST OF {this.state.listSel}</p>
                            </Row>
                            <Row>
                                <div style={{
                                    width: '137.5vmin', 
                                    height: '.8vmin', 
                                    background: 'linear-gradient(to right, #AE23A9, #DC4E1B)',
                                    marginTop: '-1vmin'
                                }}></div>
                            </Row>
                            <Row>
                                <div style={{
                                    width: '149vmin',
                                    height: 'auto',
                                    backgroundColor: 'green',
                                    marginTop: '5.1vmin'
                                }}>
                                    {
                                        this.renderItems()
                                    }
                                </div>
                            </Row>
                        </Col>
                    </FlexView>
            </div>
        );
    }
}
