import React from 'react';
import FlexView from 'react-flexview';
import './css/OptionMenu.css';
import { Col, Row } from 'react-flexbox-grid';

export default class OptionMenu extends React.Component {
    render(){
        if(this.props.sel === this.props.value){
            return(
                <FlexView hAlignContent='center' vAlignContent='center' className='container-menu'>
                    <Row>
                        <p className='opcMenu' style={{fontFamily: 'open sans light'}}>{this.props.value}</p>
                    </Row>
                </FlexView>
            );
        }else{
            this.props.setSel(this.props.value);
            return(
                <FlexView hAlignContent='center' vAlignContent='center' className='container-menu'>
                    <Row>
                        <p className='opcMenu' style={{fontFamily: 'open sans semibold'}}>{this.props.value}</p>
                    </Row>
                </FlexView>
            );
        }
    }
}
