import React from 'react';
import './css/Form.css';
import Input from './Input';
import Parse from 'parse';
import {Row, Col} from 'react-flexbox-grid';
import FlexView from 'react-flexview';

export default class Form extends React.Component{

    constructor(props){
        super(props);
        this.state = { email: '', password: '', type: 'password' };
        this.set = this.set.bind(this);
    }

    logInUser(){
        let user = Parse.User.logIn(this.state.email, this.state.password)
        .then(
            (result) => {
                alert('Welcome');
            },
            (err) => {
                alert(err.message);
            }
        );
    }

    signUpUser(){
        let user = new Parse.User();
        user.set('username', this.state.email);
        user.set('email', this.state.email);
        user.set('password', this.state.password);
        try{
            user.signUp()
            .then(
                (result) => {
                    alert("Usuario cadastrado com sucesso !!!");
                },
                (err) => {
                    alert(err);
                }
            );
        }catch(error){
            alert(error.message);
        }
    }

    set(value, typeInput){
        if(typeInput === 'email'){
            this.setState({email: value});
        }else{
            this.setState({password: value});
        }
    }

    changeVisibilityPass(){
        let type = '';
        if(this.state.type === 'password'){
            type = 'text';
        }else{
            type = 'password';
        }
        this.setState({type});
    }

    render(){
        return(
            <header className="form-render">
                <FlexView hAlignContent='center' vAlignContent='center'>
                    <Row>
                        <img src={require("../assets/logo.png")} height='24vmin'/>
                    </Row>

                    <Row>
                        <form>
                            <p>Email</p>
                            <Input 
                                id='idEmail' 
                                type='text' 
                                value={this.state.email} 
                                typeInput='email' 
                                set={this.set}
                                placeholder='seuemail@email.com'
                            />
                            <p>Senha</p>
                            <Input 
                                id='idPass' 
                                type={this.state.type} 
                                value={this.state.password} 
                                typeInput='password' 
                                set={this.set}
                                placeholder='Password'
                            />
                            <input type="checkbox" onChange={() => {this.changeVisibilityPass()}}/> Mostrar senha
                            <p>Problemas para acessar a conta ?</p>
                            <input type="button" value="Acessar" onClick={() => {this.logInUser()}}/>
                        </form>
                    </Row>

                    <Row>
                        <p>ou</p>
                    </Row>

                    <Row>
                        <input type="button" value="Cadastrar" onClick={() => {this.signUpUser()}}/>
                    </Row>
                    
                </FlexView>
            </header>
        );
    }
}
