import React from 'react';
import './css/Content.css';
import Parse from 'parse';
import MenuTop from './MenuTop';
import FlexView from 'react-flexview';
import { Col, Row } from 'react-flexbox-grid';
import ItemList from './ItemList';

export default class Content extends React.Component {

    constructor(props){
        super(props);
        this.state = { listSel: 'FOODS' };
        this.setItens = this.setItens.bind(this);
    }

    componentWillMount(){
        Parse.serverURL = 'http://localhost:1337/parse';
        Parse.initialize('OSGiFZBrXxNLjN3gYDPsgi7P4a0j6fzcc2iaCKga', 'k8xm42UVuIP51wR2DswLY8NL3zgWfev8AuKUUjga')
        this.checkLogIn();
    }

    checkLogIn(){
        let url = window.location.href;
        if(url.indexOf('?') != -1){
            let cont = url.split('?');
            let email = atob(cont[1]);
            let password = atob(cont[2]);
            Parse.User.logIn(email, password)
            .then(
                (result) => {},
                (err) => {
                    alert("Error: " + err.message);
                    window.location.href = 'http://localhost:3000';
                }
            );
        }else{
            window.location.href = 'http://localhost:3000';
        }
    }

    setItens(optSel){
        this.setState({listSel: optSel});
    }

    render(){
        return(
            <div className="content">
                    <MenuTop setItens={this.setItens} />
                    <FlexView vAlignContent="top" hAlignContent="center" className="view-content">
                        <Col style={{marginLeft: '4vmin'}}>
                            <Row>
                                <p style={{
                                    width: '134vmin', 
                                    fontFamily: 'open sans semibold', 
                                    color: '#4A4A4A',
                                    fontSize: '2.7vmin'
                                    }}>LIST OF {this.state.listSel}</p>
                            </Row>
                            <Row>
                                <div style={{
                                    width: '142.5vmin', 
                                    height: '.8vmin', 
                                    background: 'linear-gradient(to right, #AE23A9, #DC4E1B)',
                                    marginTop: '-1vmin'
                                }}></div>
                            </Row>
                            <Row>
                                <div style={{
                                    width: '149vmin',
                                    height: '100vmin',
                                    backgroundColor: 'green',
                                    marginTop: '5.1vmin'
                                }}>
                                    <ItemList />
                                    <ItemList />
                                    <ItemList />
                                </div>
                            </Row>
                        </Col>
                    </FlexView>
            </div>
        );
    }
}
